import React from "react";
import styled from "styled-components";

const Badge = styled.span`
  background-color: #000;
  border-radius: 4px;
  color: #fff;
  display: inline-block;
  font-size: 12px;
  font-weight: 500;
  letter-spacing: 1px;
  line-height: 1; 
  padding: 4px 8px;
  text-align: center;
  text-transform: uppercase;
`;

export default Badge;
